import React, { Component } from 'react';
import '../App.css';
import '../css/main.min.css';
import '../css/util.min.css';
import '../fonts/font-awesome-4.7.0/css/font-awesome.min.css';
import { Grid, Row, Col, PageHeader, Button} from 'react-bootstrap';
import { Switch, Route, Link , Redirect} from "react-router-dom";
import Select from 'react-select';
import 'react-select/dist/react-select.css';
import Parse from 'parse';
import Product from '../Product/Product.js';
class Item extends Component {
  constructor(props){
    super(props);

    this.state = { product: this.props.item, img: ''}

  }
  componentDidMount(){
    //this.uploadImage();
    //this.getImage();
    //this.setState({product: this.props.item});
  }
  uploadImage(){
    var base64 = "";
    var file = new Parse.File("myfile.jpg", { base64: base64 });
    file.save().then(function(data){
      var ClassName = Parse.Object.extend("Product");
      var _classname = new ClassName();
      _classname.id = 'vXCNXBDfcp';
      _classname.set('image',data);
      return _classname.save();
    }).then(function(data){
      console.log("succes");
    }).catch(function(e){
      console.log("failed");
    })
  }
  getImage(){
    var query = new Parse.Query('Product');
    query.equalTo('objectId','vXCNXBDfcp');
    query.first().then((data) => {
      console.log("DATA: ",data);
      console.log(data.get('image').url());
      this.setState({img: data.get('image').url()});
    }).catch(function(e){
      console.log(e)
    })
  }
  fileHandle(file){
    var ParseFile = new Parse.File(file.name,file);
    ParseFile.save().then((data) => {
      var Object = Parse.Object.extend('Product');
      var _object = new Object();
      _object.id = this.state.product;

      _object.set('image',data);
      return _object.save();
    }).catch((e) => {
      console.log(e);
    })
  }
  render(){
    return(
      <span style={{"display":"inline-block", "width":"33%"}}>
        <img src={this.props.item.get('image').url()} style={{"width":"100%"}}/>
        <div style={{"margin":"7.5px"}}>
          <a class="block2-name dis-block s-text3 p-b-5" href="#">
            <Link to={{
              pathname:"/product",
              search:this.props.item.get('name')
            }}>{this.props.item.get('name')}</Link>
          </a>
          <span>${this.props.item.get('price')}</span>
        </div>
      </span>
    )
  }
}

export default Item;
